import { useContext } from "react";
import "../assets/css/galeria.css";
import Context from "../my_context";
import Heart from "./Heart";
import Card from 'react-bootstrap/Card';


export default function Home() {

  const { images, setImages } = useContext(Context);

  const setFav = (id) => {
    const imageIndex = images.findIndex((f) => (f.id == images.id));
    images[imageIndex].fav = !images[imageIndex].fav;
    setImages([...images])


  }


  return (
    <div className="row">

      {
        images.map(({id,src,fav,alt,photographer}, i) => (

          <div className="col mb-4" key={i}>


            <div 
              onClick={()=>(setFav(id))}
              className="foto"
              style={{ backgroundImage: `url(${src.tiny})` }}>

              <Heart filled={fav}/>

              <p className="text-red" style={{ color: 'Black' }}>  {alt} - <span > {photographer}</span></p>

            </div>





          </div>
        )
        )
      }




    </div>
  );
}
